// update the PRF record when a PAF relates to a PRF
if(current.u_prf_number != ''){
	var gr = new GlideRecord("x_conym_hr_prf");
	gr.addQuery("sys_id", current.u_prf_number);
	gr.query();
	if (gr.next()) {
		gr.u_state = '100';
		gr.u_stage = 'completed';
		gr.setValue('u_work_notes', 'Position Requisition closed by Personnel Action.');
		gr.update();
	}
}
// Update position
if(current.u_maildrop != '' ){
	var gp = new GlideRecord("x_conym_hr_position");
	gp.addQuery("sys_id", current.u_maildrop);
	gp.query();
	if (gr.next()) {
		gp.u_status = 'active';
	}
	gp.update();
}

