var gr = new GlideRecord('x_conym_hr_paf');

if(current.u_activity != 'modification' ){
	gr.setValue('u_personnel_action_for', 'appointment');
}

gr.setValue('u_prf_number', current.sys_id);
gr.setValue('u_hiring_manager', current.u_hiring_manager);
gr.setValue('u_to_department', current.u_hiring_manager.department);
gr.setValue('u_functional_title', current.u_functional_title);
gr.setValue('u_corporate_title', current.u_corporate_title);
gr.setValue('u_salary_is', current.u_salary_is);
gr.u_entitlements = current.u_entitlements;
gr.u_building_access = current.u_building_access;
gr.setValue('u_state', '0');
gr.setValue('u_stage', 'request');

if(current.u_activity == 'new' || current.u_activity == 'backfill_loa' ) {
	// Create the position if it is a PRf for a new position
	var ga = new GlideRecord('x_conym_hr_position');
	ga.setValue('u_salary', current.u_desired_salary);
	ga.u_building_access = current.u_building_access;
	ga.setValue('u_corporate_title', current.u_corporate_title);
	ga.u_entitlements = current.u_entitlements;
	ga.setValue('u_full_part_time', current.position_full_part);
	ga.setValue('u_functional_title', current.u_functional_title);
	ga.setValue('u_salary_is', current.u_salary_is);
	ga.setValue('u_reg_temp', current.u_reg_temp);
	ga.setValue('u_job_description', current.u_job_description);
	ga.setValue('u_department', current.u_hiring_manager.department);
	ga.setValue('u_hiring_manager', current.u_hiring_manager);
	if(current.u_activity == 'new') {
		ga.setValue('u_maildrop', '9999999999');
	}
	
	if(current.u_activity == 'backfill_loa') {
		var temp_maildrop = current.getDisplayValue('u_maildrop') +'-T';
		ga.setValue('u_parent_position', current.getValue('u_maildrop'));
		ga.setValue('u_maildrop', temp_maildrop);
		ga.setValue('u_reg_temp', 'temporary');
		ga.u_position_active_until = current.getValue('u_return');
	}
	
	ga.setValue('u_salary_not_to_execeed', current.u_salary_not_to_exceed);
	ga.setValue('u_status', 'approved');
	ga.setValue('u_work_notes', 'Position record created');
	var gdt = new GlideDateTime();
	ga.setValue('u_effective', gdt);
	ga.insert();
	current.setValue('u_maildrop', ga.sys_id);
	// Set the PAF form
	gr.setValue('u_personnel_action_for', 'appointment');
	gr.setValue('u_maildrop', ga.sys_id);
	gr.setValue('u_salary_not_to_exceed', current.u_salary_not_to_exceed);
	gr.setValue('u_salary_is', current.u_salary_is);
	gr.setValue('u_work_notes', 'Position record created and Personnel Action record created');
	current.setValue('u_work_notes', 'Position record created and Personnel Action record created');
} else {
	gr.setValue('u_maildrop', current.u_maildrop);
	gr.setValue('u_work_notes', 'Personnel Action record created');
}

gr.insert();

